import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import archive from '../../assets/icons/albums.svg';
import Chip from '@material-ui/core/Chip';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles((theme) => ({
  image: {
    width: '92px',
    height: '78px',
  },
  Card: {
    width: "370px",
    height: "324px",
    Background: "#FFFFFF 0% 0% no-repeat padding-box",
    borderRadius: "12px",
    display: 'flex',
    justifyContent: ' center',
    // flexDirection: 'column',
    alignItems: 'center',
    marginLeft: '32px',
    '&:hover': {
      boxShadow: '0px 12px 32px #383B470F',
      border: '0.24px solid #DDDDDD80',
    },
  },
  chip: {
    marginBottom: '15px',
    marginLeft: '114px',
    width: ' 144px',
    height: '34px',
    marginTop: '11px',
    color: '#4DB6AC1F'
  },

  label: {
    color: '#4DB6AC'
  },

  paragraph: {
    font: "400 16px/24px PT Sans",
    letterSpacing: "0px",
    color: "#868E96",
    opacity: "1",
    height: '52px',
    width: "310px",
    justifyContent: 'center',
    padding: '0 30px',
    overflow: 'hidden'

  },
  paragraphs: {
    font: "400 16px/24px PT Sans",
    letterSpacing: "0px",
    color: "#868E96",
    opacity: "1",
    height: '52px',
    width: "310px",
    justifyContent: 'center',
    padding: '0 30px',
    marginBottom: '50px',
    overflow: 'hidden',
  },
  ImageCard: {
    width: '100%',
    display: 'flex',
    alignItems: ' center',
    justifyContent: 'center',
    marginTop: '50px',
  },
  title: {
    width: '100px',
    align: 'center'
  },
  fullcard: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    width: '1174px',
    padding: '0 372px 0 404px',
    height: '512px',
    background: '#FFFFFF'
  },
  h6: {
    color: '#212529',
    marginTop: '34px',
  },
  h4: {
    color: '#212529',
    marginTop: '34px',
  },


}));

const Cards = (props) => {
  const classes = useStyles(props);
  const { image, text, title, chip, datasets } = props.data;
  return (

    <div className={classes.Card} style={{backgroundColor:'red'}}  >
      <div className={classes.ImageCard}>
        <img src={image} alt="logo" className={classes.image} />
      </div>
      <Typography variant="h6" align="center" classes={{
        h6: classes.h6
      }}> {title}</Typography>
      <p className={classes.paragraph} >
        {text}
      </p>
      {datasets > 0 && (
        <Chip size="medium" label="342+ DATASETS" classes={{
          chip: classes.chip,
          label: classes.label
        }}
          label={`${datasets} DATASETS`}
          bolian="flase"
        >{chip}</Chip>
      )}
    </div>

  );
}

export default Cards;
